var websiteUrl = "http://localhost:50199/interamerican/";  function extractPageName(hrefString, sNav) { var arr = hrefString.split('/'); var arrLength = arr.length; var websiteUrlLength = websiteUrl.split('/').length; var page;  if (websiteUrlLength == arrLength) { page = (arrLength < 2) ? hrefString : arr[arrLength - 2].toLowerCase() + arr[arrLength - 1].toLowerCase(); } else { var subFolderLevels = arrLength - websiteUrlLength + 1; if (sNav != null) { if (subFolderLevels > 2) { page = (arrLength < 2) ? hrefString : arr[arrLength - subFolderLevels + 2].toLowerCase(); } else { page = (arrLength < 2) ? hrefString : arr[arrLength - subFolderLevels + 1].toLowerCase(); } } else { page = (arrLength < 2) ? hrefString : arr[arrLength - subFolderLevels].toLowerCase(); } } return page; }  function setActiveMenu(arr, crtPage, sNav) { for (var i = 0; i < arr.length; i++) { if (extractPageName(arr[i].href, sNav) == crtPage) { if (arr[i].parentNode.tagName != "li") { arr[i].parentNode.className = "active"; } return false; } } }  function highlightNavigation() {  var hrefString = document.location.href ? document.location.href : document.location;  if ($('.page-navigation').length) { setActiveMenu($('.page-navigation li a'), extractPageName(hrefString)); }  if ($('.nav-module').length) { setActiveMenu($('.mdc-leftnav2 li a'), extractPageName(hrefString, "mdc-leftnav2"), "mdc-leftnav2"); } }  $(function () { highlightNavigation(); })